mapper.select:
SELECT swift_no, tpurchase.money, tpurchase.`timestamp`, tcustomerinfo.name, tpurchase.customer_id, taccount.bank_name, tpurchase.account_id, tfunds.product_name, tpurchase.product_id FROM tpurchase JOIN tcustomerinfo ON tpurchase.customer_id = tcustomerinfo.customer_id JOIN taccount ON tpurchase.account_id = taccount.account_id JOIN tfunds ON tpurchase.product_id = tfunds.product_id
and swift_no = #{swiftNo}
and tpurchase.customer_id = #{customerId}
and name like CONCAT('%', #{customerName}, '%')
and `timestamp` > #{startTime}
and `timestamp` < #{endTime}

SELECT swift_no, share, tredeem.`timestamp`, tcustomerinfo.name, tredeem.customer_id, taccount.bank_name, tredeem.account_id, tfunds.product_name, tredeem.product_id FROM tredeem JOIN tcustomerinfo ON tredeem.customer_id = tcustomerinfo.customer_id JOIN taccount ON tredeem.account_id = taccount.account_id JOIN tfunds ON tredeem.product_id = tfunds.product_id
and swift_no = #{swiftNo}
and tredeem.customer_id = #{customerId}
and name like CONCAT('%', #{customerName}, '%')
and `timestamp` > #{startTime}
and `timestamp` < #{endTime}

SELECT taccount.account_id, bank_name, taccountchange.`timestamp`, balance_change, trade_type FROM taccountchange JOIN taccount ON taccount.account_id = taccountchange.account_id
taccount.account_id = #{accountId}
and `timestamp` > #{startTime}
and `timestamp` < #{endTime}

SELECT tcustomerinfo.customer_id, product_name, tfunds.product_id, name, taccount.account_id, taccount.bank_name, share FROM tfundsshare JOIN tcustomerinfo ON tfundsshare.customer_id = tcustomerinfo.customer_id JOIN tfunds ON tfundsshare.product_id = tfunds.product_id JOIN taccount ON tfundsshare.account_id = taccount.account_id
and name = #{customerName}
and tcustomerinfo.customer_id = #{customerId}
and tfunds.product_name like CONCAT('%',#{productName},'%')
